namespace Pimcore\Tool;

/**
 * @internal
 */
class Cast
{
    /**
     * @param string $class
     * @param object $object
     *
     * @return mixed
     */
    public static function castToClass($class, $object)
    {
        $class = ltrim($class, '\\');

        return unserialize(preg_replace('/^O:\d+:"[^"]++"/', 'O:' . strlen($class) . ':"' . $class . '"', serialize($object)));
    }
}
